import { useContext, useState } from "react";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../../context/AuthContext";
import { useToast } from "../../hooks/useToast";
import { loginUser } from "../../api/authService";

export default function Login() {
    const { login } = useContext(AuthContext); // make sure AuthContext.Provider wraps your app
    const toast = useToast(); // make sure ToastProvider wraps your app
    const navigate = useNavigate();
    const [form, setForm] = useState({ username: "", password: "" });
    const [loading, setLoading] = useState(false);

    const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        try {
            const response = await loginUser(form.username, form.password);
            if (response?.token) {
                login(response.token); // save token in AuthContext + localStorage
                toast.success("Login successful!");
                // Redirect to dashboard after successful login
                setTimeout(() => {
                    navigate("/");
                }, 500);
            } else {
                toast.error("Login failed: No token received");
            }
        } catch (err) {
            toast.error(err.message || "Invalid credentials!");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="flex items-center justify-center min-h-screen bg-gray-100">
            <form
                onSubmit={handleSubmit}
                className="max-w-md w-full bg-white p-6 rounded shadow flex flex-col gap-4"
            >
                <h1 className="text-2xl font-bold text-center mb-4">Login</h1>
                <input
                    name="username"
                    placeholder="Username"
                    value={form.username}
                    onChange={handleChange}
                    required
                    className="border p-2 rounded"
                />
                <input
                    name="password"
                    type="password"
                    placeholder="Password"
                    value={form.password}
                    onChange={handleChange}
                    required
                    className="border p-2 rounded"
                />
                <button
                    type="submit"
                    disabled={loading}
                    className="bg-indigo-600 text-white py-2 rounded hover:bg-indigo-700 transition"
                >
                    {loading ? "Logging in..." : "Login"}
                </button>
            </form>
        </div>
    );
}
