import { Input, Button } from '../ui';
import { useState, useEffect } from 'react';

export default function EmployeeForm({ initialData = {}, onSubmit, loading }) {
    const [form, setForm] = useState({
        firstName: '',
        lastName: '',
        email: '',
        position: '',
        salary: '',
        departmentId: '',
        ...initialData,
    });

    useEffect(() => {
        setForm(prevForm => ({ ...prevForm, ...initialData }));
    }, [initialData]);

    const handleChange = (e) => {
        setForm({ ...form, [e.target.name]: e.target.value });
    };

    const submitHandler = (e) => {
        e.preventDefault();
        onSubmit(form);
    };

    return (
        <form onSubmit={submitHandler} className="bg-white p-6 rounded shadow max-w-md mx-auto">
            <Input label="First Name (Required)" name="firstName" value={form.firstName} onChange={handleChange} required placeholder="Enter first name" />
            <Input label="Last Name (Required)" name="lastName" value={form.lastName} onChange={handleChange} required placeholder="Enter last name" />
            <Input label="Email Address (Required)" name="email" type="email" value={form.email} onChange={handleChange} required placeholder="Enter unique email" />
            <Input label="Position (Optional)" name="position" value={form.position} onChange={handleChange} placeholder="Enter job position" />
            <Input label="Salary (Optional)" name="salary" type="number" step="0.01" value={form.salary} onChange={handleChange} placeholder="Enter salary amount" />
            <Input label="Department ID (Optional)" name="departmentId" type="number" value={form.departmentId} onChange={handleChange} placeholder="Enter department ID" />
            <Button type="submit" className="mt-4" disabled={loading}>
                {loading ? 'Saving...' : 'Save'}
            </Button>
        </form>
    );
}
