import { useEffect, useState } from "react";
import { getAllDepartments, deleteDepartment } from "../../api/departmentService";
import { useToast } from "../../hooks/useToast";
import { useNavigate } from "react-router-dom";

export default function DepartmentList() {
    const [departments, setDepartments] = useState([]);
    const [loading, setLoading] = useState(true);
    const [deletingId, setDeletingId] = useState(null);
    const toast = useToast();
    const navigate = useNavigate();

    const fetchDepartments = async () => {
        setLoading(true);
        try {
            const data = await getAllDepartments();
            // Handle case where data might not be an array
            const departmentsArray = Array.isArray(data) ? data : [];
            setDepartments(departmentsArray);
        } catch (error) {
            console.error('Error fetching departments:', error);
            toast.error("Failed to fetch departments.");
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchDepartments();
    }, []);

    const handleDelete = async (id) => {
        if (!window.confirm("Are you sure?")) return;
        setDeletingId(id);
        try {
            await deleteDepartment(id);
            toast.success("Department deleted successfully!");
            fetchDepartments();
        } catch (error) {
            toast.error(error.message || "Failed to delete department.");
        } finally {
            setDeletingId(null);
        }
    };

    if (loading) {
        return (
            <div className="flex items-center justify-center min-h-screen">
                <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-indigo-600"></div>
            </div>
        );
    }

    return (
        <div>
            <h1 className="text-2xl font-bold mb-4">Departments</h1>
            <button
                onClick={() => navigate("/departments/add")}
                className="bg-indigo-600 text-white px-4 py-2 rounded mb-4"
            >
                Add Department
            </button>
            
            {departments.length === 0 ? (
                <div className="text-center py-8 bg-gray-50 rounded-lg">
                    <p className="text-gray-500 text-lg">No departments found</p>
                    <p className="text-gray-400 text-sm mt-2">Create your first department to get started</p>
                </div>
            ) : (
                <table className="w-full border-collapse border">
                    <thead>
                    <tr className="bg-gray-100">
                        <th className="border p-2">#</th>
                        <th className="border p-2">Department Name</th>
                        <th className="border p-2">Actions</th>
                    </tr>
                    </thead>
                    <tbody>
                    {departments.map((d, idx) => (
                        <tr key={d.id}>
                            <td className="border p-2">{idx + 1}</td>
                            <td className="border p-2">{d.name}</td>
                            <td className="border p-2 space-x-2">
                                <button
                                    onClick={() => navigate(`/departments/${d.id}/edit`)}
                                    className="bg-yellow-400 text-white px-2 py-1 rounded"
                                >
                                    Edit
                                </button>
                                <button
                                    onClick={() => handleDelete(d.id)}
                                    disabled={deletingId === d.id}
                                    className="bg-red-500 text-white px-2 py-1 rounded disabled:opacity-50"
                                >
                                    {deletingId === d.id ? 'Deleting...' : 'Delete'}
                                </button>
                            </td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            )}
        </div>
    );
}
