import api from './apiClient';

// Create leave request
export const createLeaveRequest = async (data) => {
    try {
        const response = await api.post('/leave/request', data);
        return response.data;
    } catch (error) {
        console.error('Error creating leave request:', error);
        throw error;
    }
};

// Get employee's own leave requests
export const getMyLeaveRequests = async () => {
    try {
        const response = await api.get('/leave/my-requests');
        return response.data;
    } catch (error) {
        console.error('Error fetching leave requests:', error);
        throw error;
    }
};

// Get all leave requests (for Admin/HR)
export const getAllLeaveRequests = async () => {
    try {
        const response = await api.get('/leave');
        return response.data;
    } catch (error) {
        console.error('Error fetching all leave requests:', error);
        throw error;
    }
};

// Approve leave request (for Admin/HR)
export const approveLeaveRequest = async (id) => {
    try {
        const response = await api.put(`/leave/${id}/approve`);
        return response.data;
    } catch (error) {
        console.error('Error approving leave request:', error);
        throw error;
    }
};

// Reject leave request (for Admin/HR)
export const rejectLeaveRequest = async (id) => {
    try {
        const response = await api.put(`/leave/${id}/reject`);
        return response.data;
    } catch (error) {
        console.error('Error rejecting leave request:', error);
        throw error;
    }
};
