import { useState, useEffect } from "react";
import { useNavigate, useSearchParams } from "react-router-dom";
import { useToast } from "../../hooks/useToast";
import { setupEmployeePassword } from "../../api/authService";

export default function PasswordSetup() {
    const [searchParams] = useSearchParams();
    const navigate = useNavigate();
    const toast = useToast();
    const email = searchParams.get('email');
    
    const [form, setForm] = useState({ 
        email: email || "", 
        password: "", 
        confirmPassword: "" 
    });
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        if (!email) {
            toast.error("Email is required for password setup");
            navigate("/employee-login");
        }
    }, [email, navigate, toast]);

    const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

    const validateForm = () => {
        if (!form.email) {
            toast.error("Email is required");
            return false;
        }
        if (form.password.length < 6) {
            toast.error("Password must be at least 6 characters long");
            return false;
        }
        if (form.password !== form.confirmPassword) {
            toast.error("Passwords do not match");
            return false;
        }
        return true;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        
        if (!validateForm()) return;
        
        setLoading(true);
        try {
            await setupEmployeePassword(form.email, form.password);
            toast.success("Password setup successful! Please login with your new password.");
            setTimeout(() => {
                navigate("/employee-login");
            }, 1500);
        } catch (err) {
            toast.error(err.message || "Failed to setup password");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="flex items-center justify-center min-h-screen bg-gray-100">
            <div className="max-w-md w-full">
                <div className="bg-white p-6 rounded shadow mb-4">
                    <h1 className="text-2xl font-bold text-center mb-4 text-indigo-600">Setup Password</h1>
                    <p className="text-center text-gray-600 mb-6">
                        Create your password for employee portal access
                    </p>
                    
                    <form onSubmit={handleSubmit} className="flex flex-col gap-4">
                        <div>
                            <label className="block text-sm font-medium text-gray-700 mb-1">
                                Email Address
                            </label>
                            <input
                                name="email"
                                type="email"
                                value={form.email}
                                onChange={handleChange}
                                required
                                className="w-full border p-2 rounded"
                                readOnly
                            />
                        </div>
                        
                        <div>
                            <label className="block text-sm font-medium text-gray-700 mb-1">
                                New Password
                            </label>
                            <input
                                name="password"
                                type="password"
                                placeholder="Enter new password"
                                value={form.password}
                                onChange={handleChange}
                                required
                                className="w-full border p-2 rounded"
                            />
                        </div>
                        
                        <div>
                            <label className="block text-sm font-medium text-gray-700 mb-1">
                                Confirm Password
                            </label>
                            <input
                                name="confirmPassword"
                                type="password"
                                placeholder="Confirm new password"
                                value={form.confirmPassword}
                                onChange={handleChange}
                                required
                                className="w-full border p-2 rounded"
                            />
                        </div>
                        
                        <button
                            type="submit"
                            disabled={loading}
                            className="bg-indigo-600 text-white py-2 rounded hover:bg-indigo-700 transition disabled:bg-gray-400"
                        >
                            {loading ? "Setting up..." : "Setup Password"}
                        </button>
                    </form>
                </div>
                
                <div className="bg-white p-4 rounded shadow text-center">
                    <p className="text-sm text-gray-600">
                        Already have a password?{" "}
                        <button
                            onClick={() => navigate("/employee-login")}
                            className="text-indigo-600 hover:underline font-medium"
                        >
                            Login Here
                        </button>
                    </p>
                </div>
            </div>
        </div>
    );
}
