import { Input, Button, Select } from '../ui';
import { useState, useEffect } from 'react';

export default function UserForm({ initialData = {}, onSubmit, loading }) {
    const [form, setForm] = useState({
        name: '',
        email: '',
        role: 'user',
        password: '',
        ...initialData,
    });

    useEffect(() => {
        setForm({ ...form, ...initialData });
    }, [initialData]);

    const handleChange = (e) => {
        setForm({ ...form, [e.target.name]: e.target.value });
    };

    const submitHandler = (e) => {
        e.preventDefault();
        onSubmit(form);
    };

    return (
        <form onSubmit={submitHandler} className="bg-white p-6 rounded shadow max-w-md mx-auto">
            <Input label="Name" name="name" value={form.name} onChange={handleChange} required />
            <Input label="Email" name="email" value={form.email} onChange={handleChange} required />
            {!initialData.id && <Input label="Password" name="password" type="password" value={form.password} onChange={handleChange} required />}
            <Select
                label="Role"
                name="role"
                value={form.role}
                onChange={handleChange}
                options={[
                    { label: 'User', value: 'user' },
                    { label: 'Admin', value: 'admin' },
                ]}
            />
            <Button type="submit" className="mt-4" disabled={loading}>
                {loading ? 'Saving...' : 'Save'}
            </Button>
        </form>
    );
}
