import { Input, Button } from '../ui';
import { useState, useEffect } from 'react';

export default function EmployeeForm({ initialData = {}, onSubmit, loading }) {
    const [form, setForm] = useState({
        name: '',
        email: '',
        phone: '',
        departmentId: '',
        ...initialData,
    });

    useEffect(() => {
        setForm({ ...form, ...initialData });
    }, [initialData]);

    const handleChange = (e) => {
        setForm({ ...form, [e.target.name]: e.target.value });
    };

    const submitHandler = (e) => {
        e.preventDefault();
        onSubmit(form);
    };

    return (
        <form onSubmit={submitHandler} className="bg-white p-6 rounded shadow max-w-md mx-auto">
            <Input label="Name" name="name" value={form.name} onChange={handleChange} required />
            <Input label="Email" name="email" value={form.email} onChange={handleChange} required />
            <Input label="Phone" name="phone" value={form.phone} onChange={handleChange} />
            <Input label="Department ID" name="departmentId" value={form.departmentId} onChange={handleChange} />
            <Button type="submit" className="mt-4" disabled={loading}>
                {loading ? 'Saving...' : 'Save'}
            </Button>
        </form>
    );
}
