import api from './apiClient';

// Get all users
export const getUsers = async () => (await api.get('/users')).data;

// Alias for consistency
export const getAllUsers = getUsers;

// Get single user
export const getUser = async (id) => (await api.get(`/users/${id}`)).data;

// Create new user
export const createUser = async (data) => (await api.post('/users/create', data)).data;

// Update existing user
export const updateUser = async (id, data) => (await api.put(`/users/${id}`, data)).data;

// Delete user
export const deleteUser = async (id) => (await api.delete(`/users/${id}`)).data;
