import { Input, Button } from '../ui';
import { useState, useEffect } from 'react';

export default function DepartmentForm({ initialData = {}, onSubmit, loading }) {
    const [form, setForm] = useState({
        name: '',
        description: '',
        ...initialData,
    });

    useEffect(() => {
        setForm(prevForm => ({ ...prevForm, ...initialData }));
    }, [initialData]);

    const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

    const submitHandler = (e) => {
        e.preventDefault();
        onSubmit(form);
    };

    return (
        <form onSubmit={submitHandler} className="bg-white p-6 rounded shadow max-w-md mx-auto">
            <Input label="Name" name="name" value={form.name} onChange={handleChange} required />
            <Input label="Description" name="description" value={form.description} onChange={handleChange} />
            <Button type="submit" className="mt-4" disabled={loading}>
                {loading ? 'Saving...' : 'Save'}
            </Button>
        </form>
    );
}
