import { Input, Button, Select } from '../ui';
import { useState, useEffect } from 'react';

export default function AttendanceForm({ initialData = {}, onSubmit, loading }) {
    const [form, setForm] = useState({
        employeeId: '',
        checkIn: '',
        checkOut: '',
        ...initialData,
    });

    useEffect(() => {
        setForm(prevForm => ({ ...prevForm, ...initialData }));
    }, [initialData]);

    const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

    const submitHandler = (e) => {
        e.preventDefault();
        onSubmit(form);
    };

    return (
        <form onSubmit={submitHandler} className="bg-white p-6 rounded shadow max-w-md mx-auto">
            <Input label="Employee ID (Required)" name="employeeId" type="number" value={form.employeeId} onChange={handleChange} required placeholder="Enter employee ID" />
            <Input label="Check In Time (Required)" name="checkIn" type="datetime-local" value={form.checkIn} onChange={handleChange} required placeholder="Select check-in time" />
            <Input label="Check Out Time (Optional)" name="checkOut" type="datetime-local" value={form.checkOut} onChange={handleChange} placeholder="Select check-out time" />
            <Button type="submit" className="mt-4" disabled={loading}>
                {loading ? 'Saving...' : 'Save'}
            </Button>
        </form>
    );
}
