export default function Select({ className, options = [], ...props }) {
    return (
        <select
            {...props}
            className={`border p-2 rounded focus:ring-2 focus:ring-indigo-500 ${className}`}
        >
            {options.map((option, index) => (
                <option key={index} value={option.value}>
                    {option.label}
                </option>
            ))}
        </select>
    );
}
