import { Input, Button, Select } from '../ui';
import { useState, useEffect } from 'react';
import { getDepartments } from '../../api/departmentService';

export default function EmployeeForm({ initialData = {}, onSubmit, loading }) {
    const [form, setForm] = useState({
        firstName: '',
        lastName: '',
        email: '',
        position: '',
        salary: '',
        departmentId: '',
        ...initialData,
    });
    
    const [departments, setDepartments] = useState([]);
    const [loadingDepartments, setLoadingDepartments] = useState(false);

    useEffect(() => {
        setForm(prevForm => ({ ...prevForm, ...initialData }));
    }, [initialData]);

    useEffect(() => {
        const fetchDepartments = async () => {
            setLoadingDepartments(true);
            try {
                const data = await getDepartments();
                setDepartments(Array.isArray(data) ? data : []);
            } catch (error) {
                console.error('Error fetching departments:', error);
                setDepartments([]);
            } finally {
                setLoadingDepartments(false);
            }
        };
        
        fetchDepartments();
    }, []);

    const handleChange = (e) => {
        setForm({ ...form, [e.target.name]: e.target.value });
    };

    const submitHandler = (e) => {
        e.preventDefault();
        onSubmit(form);
    };

    return (
        <form onSubmit={submitHandler} className="bg-white p-8 rounded-lg shadow-lg max-w-2xl mx-auto">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div className="space-y-4">
                    <Input label="First Name (Required)" name="firstName" value={form.firstName} onChange={handleChange} required placeholder="Enter first name" />
                    <Input label="Last Name (Required)" name="lastName" value={form.lastName} onChange={handleChange} required placeholder="Enter last name" />
                    <Input label="Email Address (Required)" name="email" type="email" value={form.email} onChange={handleChange} required placeholder="Enter unique email" />
                </div>
                <div className="space-y-4">
                    <Input label="Position (Optional)" name="position" value={form.position} onChange={handleChange} placeholder="Enter job position" />
                    <Input label="Salary (Optional)" name="salary" type="number" step="0.01" value={form.salary} onChange={handleChange} placeholder="Enter salary amount" />
                    <Select
                        label="Department (Optional)"
                        name="departmentId"
                        value={form.departmentId}
                        onChange={handleChange}
                        loading={loadingDepartments}
                        options={departments.map(dept => ({
                            label: dept.name,
                            value: dept.id.toString()
                        }))}
                        placeholder="Select department"
                    />
                </div>
            </div>
            <Button type="submit" className="mt-4" disabled={loading}>
                {loading ? 'Saving...' : 'Save'}
            </Button>
        </form>
    );
}
