import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import { useContext } from "react";
import { AuthContext } from "../context/AuthContext";

// Layouts
import ProtectedLayout from "../components/layout/ProtectedLayout";

// Pages
import Login from "../pages/login/login";
import Dashboard from "../pages/dashboard/Dashboard";

// Employees
import EmployeeList from "../pages/employees/EmployeeList";
import EmployeeAdd from "../pages/employees/EmployeeAdd";
import EmployeeEdit from "../pages/employees/EmployeeEdit";

// Users
import UserList from "../pages/users/UserList";
import UserAdd from "../pages/users/UserAdd";
import UserEdit from "../pages/users/UserEdit";

// Departments
import DepartmentList from "../pages/departments/DepartmentList";
import DepartmentAdd from "../pages/departments/DepartmentAdd";
import DepartmentEdit from "../pages/departments/DepartmentEdit";

// Attendance & Leave
import AttendanceList from "../pages/attendance/AttendanceList";
import AttendanceAdd from "../pages/attendance/AttendanceAdd";
import AttendanceEdit from "../pages/attendance/AttendanceEdit";
import LeaveRequests from "../pages/attendance/LeaveRequests";

// Notifications
import NotificationList from "../pages/notification/NotificationList";
import NotificationSend from "../pages/notification/NotificationSend";

export default function AppRouter() {
    const { user } = useContext(AuthContext);

    // Protect routes
    const RequireAuth = ({ children }) => {
        if (!user) return <Navigate to="/login" />;
        return children;


    };

    return (
        <Router>
            <Routes>
                {/* Login */}
                <Route path="/login" element={<Login />} />

                {/* Dashboard */}
                <Route
                    path="/"
                    element={
                        <RequireAuth>
                            <ProtectedLayout>
                                <Dashboard />
                            </ProtectedLayout>
                        </RequireAuth>
                    }
                />

                {/* Employees */}
                <Route
                    path="/employees"
                    element={
                        <RequireAuth>
                            <ProtectedLayout>
                                <EmployeeList />
                            </ProtectedLayout>
                        </RequireAuth>
                    }
                />
                <Route
                    path="/employees/add"
                    element={
                        <RequireAuth>
                            <ProtectedLayout>
                                <EmployeeAdd />
                            </ProtectedLayout>
                        </RequireAuth>
                    }
                />
                <Route
                    path="/employees/:id/edit"
                    element={
                        <RequireAuth>
                            <ProtectedLayout>
                                <EmployeeEdit />
                            </ProtectedLayout>
                        </RequireAuth>
                    }
                />

                {/* Users */}
                <Route
                    path="/users"
                    element={
                        <RequireAuth>
                            <ProtectedLayout>
                                <UserList />
                            </ProtectedLayout>
                        </RequireAuth>
                    }
                />
                <Route
                    path="/users/add"
                    element={
                        <RequireAuth>
                            <ProtectedLayout>
                                <UserAdd />
                            </ProtectedLayout>
                        </RequireAuth>
                    }
                />
                <Route
                    path="/users/:id/edit"
                    element={
                        <RequireAuth>
                            <ProtectedLayout>
                                <UserEdit />
                            </ProtectedLayout>
                        </RequireAuth>
                    }
                />

                {/* Departments */}
                <Route
                    path="/departments"
                    element={
                        <RequireAuth>
                            <ProtectedLayout>
                                <DepartmentList />
                            </ProtectedLayout>
                        </RequireAuth>
                    }
                />
                <Route
                    path="/departments/add"
                    element={
                        <RequireAuth>
                            <ProtectedLayout>
                                <DepartmentAdd />
                            </ProtectedLayout>
                        </RequireAuth>
                    }
                />
                <Route
                    path="/departments/:id/edit"
                    element={
                        <RequireAuth>
                            <ProtectedLayout>
                                <DepartmentEdit />
                            </ProtectedLayout>
                        </RequireAuth>
                    }
                />

                {/* Attendance */}
                <Route
                    path="/attendance"
                    element={
                        <RequireAuth>
                            <ProtectedLayout>
                                <AttendanceList />
                            </ProtectedLayout>
                        </RequireAuth>
                    }
                />
                <Route
                    path="/attendance/add"
                    element={
                        <RequireAuth>
                            <ProtectedLayout>
                                <AttendanceAdd />
                            </ProtectedLayout>
                        </RequireAuth>
                    }
                />
                <Route
                    path="/attendance/:id/edit"
                    element={
                        <RequireAuth>
                            <ProtectedLayout>
                                <AttendanceEdit />
                            </ProtectedLayout>
                        </RequireAuth>
                    }
                />
                <Route
                    path="/attendance/leave"
                    element={
                        <RequireAuth>
                            <ProtectedLayout>
                                <LeaveRequests />
                            </ProtectedLayout>
                        </RequireAuth>
                    }
                />

                {/* Notifications */}
                <Route
                    path="/notifications"
                    element={
                        <RequireAuth>
                            <ProtectedLayout>
                                <NotificationList />
                            </ProtectedLayout>
                        </RequireAuth>
                    }
                />
                <Route
                    path="/notifications/send"
                    element={
                        <RequireAuth>
                            <ProtectedLayout>
                                <NotificationSend />
                            </ProtectedLayout>
                        </RequireAuth>
                    }
                />

                {/* Redirect any unknown route to dashboard */}
                <Route path="*" element={<Navigate to="/" />} />
            </Routes>
        </Router>
    );
}
