import { useContext, useState } from "react";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../../context/AuthContext";
import { useToast } from "../../hooks/useToast";
import { loginUser } from "../../api/authService";

export default function EmployeeLogin() {
    const { login } = useContext(AuthContext);
    const toast = useToast();
    const navigate = useNavigate();
    const [form, setForm] = useState({ email: "", password: "" });
    const [loading, setLoading] = useState(false);

    const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        try {
            const response = await loginUser(form.email, form.password);
            if (response?.token) {
                const userData = login(response.token);
                
                // Check if the user has EMPLOYEE role
                if (userData?.role === 'USER') {
                    toast.success("Login successful!");
                    setTimeout(() => {
                        navigate("/my-profile");
                    }, 500);
                } else {
                    toast.error("This login is for employees only. Please use the admin login.");
                    // Logout the user immediately
                    const { logout } = useContext(AuthContext);
                    logout();
                }
            } else {
                toast.error("Login failed: No token received");
            }
        } catch (err) {
            // Check if error is about missing password
            if (err.message && err.message.includes("password not set")) {
                // Redirect to password setup page with email
                navigate(`/setup-password?email=${encodeURIComponent(form.email)}`);
            } else {
                toast.error(err.message || "Invalid credentials!");
            }
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="flex items-center justify-center min-h-screen bg-gray-100">
            <div className="max-w-md w-full">
                <div className="bg-white p-6 rounded shadow mb-4">
                    <h1 className="text-2xl font-bold text-center mb-4 text-indigo-600">Employee Portal</h1>
                    <p className="text-center text-gray-600 mb-6">Login to access your employee dashboard</p>
                    
                    <form onSubmit={handleSubmit} className="flex flex-col gap-4">
                        <input
                            name="email"
                            type="email"
                            placeholder="Email Address"
                            value={form.email}
                            onChange={handleChange}
                            required
                            className="border p-2 rounded"
                        />
                        <input
                            name="password"
                            type="password"
                            placeholder="Password"
                            value={form.password}
                            onChange={handleChange}
                            required
                            className="border p-2 rounded"
                        />
                        <button
                            type="submit"
                            disabled={loading}
                            className="bg-indigo-600 text-white py-2 rounded hover:bg-indigo-700 transition"
                        >
                            {loading ? "Logging in..." : "Login as Employee"}
                        </button>
                    </form>
                </div>
                
                <div className="bg-white p-4 rounded shadow text-center">
                    <div className="mb-3">
                        <p className="text-sm text-gray-600 mb-2">First time logging in?</p>
                        <button
                            onClick={() => navigate("/setup-password")}
                            className="text-indigo-600 hover:underline font-medium text-sm"
                        >
                            Create Your Password
                        </button>
                    </div>
                    
                    <div className="border-t pt-3">
                        <p className="text-sm text-gray-600">
                            Are you an administrator?{" "}
                            <button
                                onClick={() => navigate("/login")}
                                className="text-indigo-600 hover:underline font-medium"
                            >
                                Admin Login
                            </button>
                        </p>
                    </div>
                </div>
            </div>
        </div>
    );
}
