import { Input, Button, Select } from '../ui';
import { useState, useEffect } from 'react';

export default function AttendanceForm({ initialData = {}, onSubmit, loading }) {
    const [form, setForm] = useState({
        userId: '',
        date: '',
        status: 'present',
        ...initialData,
    });

    useEffect(() => {
        setForm({ ...form, ...initialData });
    }, [initialData]);

    const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

    const submitHandler = (e) => {
        e.preventDefault();
        onSubmit(form);
    };

    return (
        <form onSubmit={submitHandler} className="bg-white p-6 rounded shadow max-w-md mx-auto">
            <Input label="User ID" name="userId" value={form.userId} onChange={handleChange} required />
            <Input label="Date" type="date" name="date" value={form.date} onChange={handleChange} required />
            <Select
                label="Status"
                name="status"
                value={form.status}
                onChange={handleChange}
                options={[
                    { label: 'Present', value: 'present' },
                    { label: 'Absent', value: 'absent' },
                    { label: 'On Leave', value: 'leave' },
                ]}
            />
            <Button type="submit" className="mt-4" disabled={loading}>
                {loading ? 'Saving...' : 'Save'}
            </Button>
        </form>
    );
}
